package operators;

public class Example1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("Program Starts");
// Variables Declaration and Initialization
int num1=20, num2= 10;
//Addition Operation
int result=num1+num2;
System.out.println("Addition of Two No.s is: "+result);

//Subtraction Operation
result=num1-num2;
System.out.println("Substraction of Two No.s is: "+result);

//Multiplication Operation
result=num1*num2;
System.out.println("Multiplication of Two No.s is: "+result);

//Division Operation
result=num1/num2;
System.out.println("Quotient of Two Divided No.s is: "+result);

//Modulus Operation
result=num1%num2;
System.out.println("Remainder of Two Modulus No.s is: "+result);
System.out.println("Program Ends");
	}
}